        lower = np.nanpercentile(numeric, 1)
        upper = np.nanpercentile(numeric, 99)
        original_min = float(np.nanmin(numeric))
        original_max = float(np.nanmax(numeric))
        clipped = numeric.clip(lower, upper)
        info = {
            "lower_bound": float(lower),
